import styled from 'styled-components';

export const CardWrapper = styled.div`
  display: grid;
  justify-content: center;
  align-content: center;
  position: relative;
  padding-top: 15px;
  padding-bottom: 30px;
  overflow: visible;
  width: 100%;
  height: auto;
`;

export const Card = styled.div`
  box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.16), 0 2px 10px 0 rgba(0, 0, 0, 0.12);
  position: relative;
  transition: box-shadow 0.25s;
  -webkit-transition: box-shadow 0.25s;
  border-radius: 10px;
  margin: 8px 0;
  width: 100%;
  height: 400px;
  overflow: hidden;
  -webkit-transition: height 1s, color 1s, margin 1s;
  transition: height 1s, color 1s, margin 1s;
  z-index: 1;
  background: #06a763;
`;

export const Profile = styled.div`
  position: absolute;
  right: 0;
  z-index: 1;
  float: right !important;
  img {
    width: 400px;
    height: auto;
    display: block;
    max-width: 100%;
  }
  div {
    top: 0;
    z-index: 1;
    position: absolute;
    border-style: solid;
    border-width: 400px 0 0 100px;
    border-color: rgba(223, 50, 50, 0) transparent transparent #06a763;
  }
`;

export const CardContent = styled.div`
  padding: 10px;
  border-radius: 0 0 2px 2px;
  p {
    margin: 0;
    color: inherit;
    text-align: center;
  }
`;
export const InfoHeadings = styled.div`
  max-width: 514px;
  h4 {
    font-size: 1.5rem;
    padding-left: 3rem;
    text-align: left;
    text-transform: uppercase;
    display: block;
    width: 100%;
    font-weight: 900;
    color: #fff;
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.29);
  }
  h6 {
    font-size: 1.2rem;
    padding-left: 3rem;
    text-align: left;
    text-transform: capitalize;
    display: block;
    width: 100%;
    font-weight: 300;
    margin-top: -30px;
    margin-bottom: 30px;
    color: #fff;
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.29);
  }
`;
export const InfoDetails = styled.div`
  display: table;
  margin: 0;
  max-width: 100%;
  padding: 0;
  max-width: 515px;
  margin-top: -30px;
  ul {
    text-align: left;
    list-style-type: none;
    li {
      clear: both;
      span {
        display: block;
        float: left;
        color: #fff;
        line-height: 22px;
        font-size: 15px;
        font-weight: 400;
        color: #fff;
        i {
          padding-left: 2px;
        }
      }
    }
  }
`;

export const Container = styled.div`
  padding: 0 15px;
  margin: 0 auto;
  @media (min-width: 768px) {
    width: 750px;
  }

  @media (min-width: 992px) {
    width: 970px;
  }

  @media (min-width: 1200px) {
    width: 1170px;
  }
`;
