import { createGlobalStyle } from 'styled-components';
import MuliRegularTTF from './fonts/Muli/Muli-Regular.ttf';
import RalewayMediumTTF from './fonts/Raleway-Font/Raleway-Medium.ttf';
export const GlobalStyles = createGlobalStyle`
@font-face {
  font-family: 'Raleway';
  src: local('Raleway'), local('Raleway'),
  url(${RalewayMediumTTF}) format('ttf'),
  font-weight: 400;
  font-style: Medium;
}
@font-face {
  font-family: 'Muli';
  src: local('Muli'), local('Muli'),
  url(${MuliRegularTTF}) format('ttf'),
  font-weight: 400;
  font-style: Medium;
}
  html, body {
    margin: 0;
    padding: 0;
  }
  *, *::after, *::before {
    box-sizing: border-box;
  }
  body {
    background: ${({ theme }) => theme.colors.primaryDark};
    color: ${({ theme }) => theme.colors.primaryLight};
    height: 100vh;
    text-rendering: optimizeLegibility;
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
  }
  h1 {
    font-size: 2rem;
    text-align: center;
    text-transform: uppercase;
  }
  img {
    border-radius: 5px;
    height: auto;
    width: 10rem;
  }
  div {
    text-align: center;
  }
  small {
    display: block;
  }
  a {
    color: ${({ theme }) => theme.colors.primaryHover};
    text-decoration: none;
  }
`;
